function showVideoModal(state = {
  showing: false,
  videoId: ''
}, action) {
  switch(action.type) {
    case OPEN_MODAL_VIDEO:
      return {
        showing: true,
        videoId: action.videoId
      }
    case CLOSE_MODAL_VIDEO:
      return {
        showing: false,
        videoId: ''
      }
    default:
      return state;
  }
}

const rootReducer = combineReducers({
  categories,
  featuredMovie,
  latestMovies,
  movies,
  movieDetails,
  users,
  userLinks,
  showVideoModal
});

export default rootReducer;